package java_package;

public class Forloopevenandnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for (int i=20;i<=40;i++)
	
{
	if (i%2==0)
	{
		System.out.println("It is an even number "+i);
	}	
	else
	{
		System.out.println("It is odd number "+i);
	}
}
	
	}
	

}
