package java_package;

public class IfElseProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =21;
		
		if(a%2 == 0)
		{
			System.out.println("This is a even number "+a);
		}
		else
		{
			System.out.println("This is a odd number "+a);
		}
	}

}
